import './SignUP.css';
import { useNavigate } from 'react-router-dom';
import React, { useState,} from 'react';
import axios from 'axios';

const SignUp = () => {
  const [email, getEmail] = useState('');
  const [password, getPassword] = useState('');
  const [username, getUsername] = useState('');

  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();
  
  const handleLogin = async () => {
    try {
      await axios.post('http://localhost:5000/login', { email, password, username });
      console.log('login success');
      navigate('/');
    } catch (error) {
        console.log('terjadi error masbro ');
      if (error.response && error.response.data) {
        setErrorMessage(error.response.data.message);
      } else {
        setErrorMessage('Terjadi kesalahan saat melakukan login');
      }
    }
  };



    return (
        <div className="page">
       <div className="cover1">
        <h1>Sign Up</h1>
        <div className='Sign'>
            <input type="text" placeholder="Username" value={username} onChange={(e) => getUsername(e.target.value)} /><br />
            <input type="text" placeholder="Email" value={email} onChange={(e) => getEmail(e.target.value)} /><br />
            <input type="password" placeholder="Password" value={password} onChange={(e) => getPassword(e.target.value)} />
        </div>

        <button className='Sign-up' >
        Sign UP
        </button>
       
    
       </div>
       </div>
       
